export default function Button({ title, color, disabled, ...props }) {
  return (
    <React.Fragment>
      <button
        disabled={disabled}
        style={{
          color: color,
          opacity: disabled ? 0.5 : 1,
        }}
        className="w-fit py-3 border-2 px-7 hover:bg-black hover:text-white border-black"
        {...props}
      >
        {title}
      </button>
    </React.Fragment>
  );
}
