      <button onClick={handleCount} className="bg-gray-400 px-5 p-2 rounded-xl">
        Klik
      </button>
      <div className="space-x-2 py-5">
        <button
          className="bg-blue-500 px-5 p-2 rounded-xl"
          onClick={() => {
            setColor("blue");
            console.log(color);
          }}
        >
          Blue
        </button>
        <button
          className="bg-red-500 px-5 p-2 rounded-xl"
          onClick={() => {
            setColor("red");
            console.log(color);
          }}
        >
          Red
        </button>
        <button
          className="bg-green-500 px-5 p-2 rounded-xl"
          onClick={() => {
            setColor("green");
            console.log(color);
          }}
        >
          Green
        </button>
      </div>
      <div className="py-10">
        <MemorizeChild color={color} count={handleCount} />
      </div>{" "}
    </div>
  );
}

