import React, { memo, useCallback } from "react";

export default function Parent() {
  const [count, setCount] = React.useState(0);
  const [user, setUser] = React.useState("ihsan");
  const [color, setColor] = React.useState("red");
  console.log("parent render");
  const handleCount = useCallback(() => {
    setCount((c) => c + 1);
  }, []);
  const prosesGerak = (count) => {
    // console.log("Parent Render");
    delay(2000);
    return count + 1;
  };
  // const handleCallBack = useCallback(handleCount, []);
  return (
    <div className="p-10">
      <h1 className={`text-[${color}]-500`}>Parent</h1>
      <p>count : {count}</p>
      <p>count 2 : {prosesGerak()}</p>
      <button onClick={handleCount} className="bg-gray-400 px-5 p-2 rounded-xl">
        Klik
      </button>
      <div className="space-x-2">
        <button
          className="bg-gray-400 px-5 p-2 rounded-xl"
          onClick={() => {
            setColor("blue");
            console.log(color);
          }}
        >
          Blue
        </button>
        <button
          className="bg-gray-400 px-5 p-2 rounded-xl"
          onClick={() => {
            setColor("red");
          }}
        >
          Red
        </button>
        <button
          className="bg-gray-400 px-5 p-2 rounded-xl"
          onClick={() => {
            setColor("green");
          }}
        >
          Green
        </button>
      </div>

      <MemorizeChild user={user} count={handleCount} />
    </div>
  );
}

function Child({ user, count }) {
  // delay(2000);
  console.log("child render");
  return (
    <div>
      <p>Child</p>
      <p>User : {user}</p>
      <button className="bg-red-400 px-5 rounded-xl p-2" onClick={count}>
        Tambah Count
      </button>
    </div>
  );
}

const MemorizeChild = memo(Child);

function delay(milliseconds) {
  const date = Date.now();
  let currentDate = null;
  if(milliseconds < 200000){}
}
