import React from "react";
import { useEffect, useState } from "react";

export default function RefTutor() {
  const [Name, setName] = useState("");
  const inputRef = React.useRef(null);
  const count = React.useRef(0);
  useEffect(() => {
    count.current = count.current + 1;
  }, []);
  const click = () => {
    // inputRef.current.focus();
    inputRef.current = "saya adalah athfa";
    console.log(inputRef);
    console.log(inputRef.className);
    console.log(inputRef.current);
    inputRef.className = "border p-4 border-red-400";
  };

  return (
    <div>
      <input
        type="text"
        ref={inputRef}
        className={inputRef.className}
        value={Name}
        onChange={(e) => {
          return setName(e.target.value = inputRef.current);
        }}
      />
      <div>
        <p>{Name}</p>
        <p>{inputRef.current}</p>
      </div>
      <div>
        <p>{count.current}</p>
        <button onClick={click}>Click</button>
      </div>
    </div>
  );
}
