import React, { memo, useCallback } from "react";

export default function Parent() {
  const [count, setCount] = React.useState(0);
  const [user, setUser] = React.useState("ihsan");
  console.log("parent render");
  const handleCount = useCallback(() => {
    setCount((c) => c + 1);
  }, []);
  // const handleCallBack = useCallback(handleCount, []);
  return (
    <div className="p-10">
      <h1>Parent</h1>
      <p>count : {count}</p>
      <button onClick={handleCount} className="bg-red-400 px-5 p-2 rounded-xl ">
        Klik
      </button>

      <MemorizeChild user={user} count={handleCount} />
    </div>
  );
}

function Child({ user, count }) {
  // delay(2000);
  console.log("child render");
  return (
    <div>
      <p>Child</p>
      <p>User : {user}</p>
      <button className="bg-red-400 px-5 rounded-xl p-2" onClick={count}>
        Tambah Count
      </button>
    </div>
  );
}

const MemorizeChild = memo(Child);

function delay(milliseconds) {
  const date = Date.now();
  let currentDate = null;
  do {
    currentDate = Date.now();
  } while (currentDate - date < milliseconds);
}
