import React, { useReducer } from "react";

const reducer = (state, action) => {
  if (action.type === "increment") {
    return {
      count: state.count + 1,
      showText: (state.showText = true),
    };
  }
  if (action.type === "decrement") {
    return {
      count: state.count - 1,
      showText: (state.showText = false),
    };
  }
  if (action.type === "toggleShowText") {
    return {
      count: state.count,
      showText: state.showText,
    };
  }
  return state;
};

export default function ReducerMateri() {
  const [state, dispatch] = useReducer(reducer, { count: 0, showText: true });
  return (
    <div>
      <h1>{state.count}</h1>
      <div className="">
        <button
          onClick={() => {
            dispatch({ type: "increment" });
            dispatch({ type: "toggleShowText" });
          }}
        >
          Tambah
        </button>
        <button
          onClick={() => {
            dispatch({ type: "decrement" });
            dispatch({ type: "toggleShowText" });
          }}
        >
          Kurang
        </button>
      </div>
      {state.showText ? <p>Bertambah</p> : <p>Berkurang</p>}
    </div>
  );
}
