import React, { useRef } from "react";

const UseRef = () => {
  const homeRef = useRef(null);
  const aboutRef = useRef(null);
  const topRef = useRef(null);

  const scrollToSection = (ref) => {
    window.scrollTo({
      top: ref.current.offsetTop,
      behavior: "smooth",
    });
    console.log(window)
  };
  const scrollToTop = (ref) => {
    window.scrollTo({
      top: ref.current.offsetTop,
      behavior: "smooth",
    });
  };
  return (
    <section className="flex justify-between my-5 w-screen">

      <div ref={topRef} className="w-full">
        <div className="space-x-5">
          <button
            onClick={() => {
              scrollToSection(homeRef);
            }}
          >
            Home
          </button>
          <button
            onClick={() => {
              scrollToSection(aboutRef);
            }}
          >
            About
          </button>
        </div>

        <div ref={homeRef} className="h-screen w-full bg-red-500 pt-10">
          <h1 className="text-white font-bold">Home</h1>
        </div>
        <div ref={aboutRef} className="h-screen w-full bg-blue-500 pt-10">
          <h1 className="text-white font-bold">About</h1>
        </div>

        <div>
          <button
            onClick={() => {
              scrollToTop(topRef);
            }}
            className="fixed bg-green-500 cursor-pointer z-10 bottom-2 rounded-xl p-5 right-5"
          >
            UP
          </button>
        </div>
      </div>
    </section>
  );
};

export default UseRef;
