import React from "react";
import { useNavigate } from "react-router-dom";
import Button from "../../komponen/button";
import Cookies from "js-cookie";
import Input from "../../komponen/input";
import { loginProses } from "../../API/auth";
import { authLogin, authRegister } from "../redux/action/authAction";
import { useDispatch } from "react-redux";

const Register = () => {
  let dispatch = useDispatch();
  const navigate = useNavigate();
  const [payload, setPayload] = React.useState({
    name: "",
    email: "",
    password: "",
    password_confirmation: "",
  });
  const handleChange = (e) => {
    e.preventDefault();
    setPayload((payload) => {
      return {
        ...payload,
        [e.target.name]: e.target.value,
      };
    });
  };
  const [messageError, setMessageError] = React.useState(false);
  const [ErrorEmail, setErrorEmail] = React.useState(false);
  const [ErrorName, setErrorName] = React.useState(false);
  const [ErrorPassword, setErrorPassword] = React.useState(false);
  const [ErrorPasswordConfirm, setErrorPasswordConfirm] = React.useState(false);
  const [isLoading, setIsLoading] = React.useState(false);
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setIsLoading(true);
      const response = await dispatch(authRegister(payload));
      console.log("response", response);
      // return navigate("/article", { replace: true });
      if (response?.status === "Success") {
        return navigate("/article", { replace: true });
      } else {
        setMessageError(response?.response?.data?.message);
        setErrorEmail(response?.response?.data?.errors.email);
        setErrorName(response?.response?.data?.errors.name);
        setErrorPassword(response?.response?.data?.errors.password);
        setErrorPasswordConfirm(
          response?.response?.data?.errors.password_confirmation
        );
      }
    } catch (err) {
      console.log("error =>", err);
    } finally {
      setIsLoading(false);
    }

    setPayload(() => {
      return {
        name: "",
        email: "",
        password: "",
        password_confirmation: "",
      };
    });
  };
  return (
    <div className="mt-5 flex flex-col justify-center items-center ">
      <p>{}</p>
      <h1>login page</h1>
      <div className="absolute w-60 top-9 right-2 p-5 border-2 space-y-3">
        <p className="font-bold">Alert!</p>
        <p className="text-red-500">{messageError}</p>
        <p className="text-red-500">{ErrorName}</p>
        <p className="text-red-500">{ErrorEmail}</p>
        <p className="text-red-500">{ErrorPassword}</p>
        <p className="text-red-500">{ErrorPasswordConfirm}</p>
      </div>

    <div className="border-black border-2 w-[25rem]  px-5 py-5 mt-5 ">
        <form action="" onSubmit={handleSubmit} className="space-y-5">
          <Input
            value={payload.name}
            placeholder={"Name"}
            onChange={handleChange}
            type="text"
            name={"name"}
          />

          <Input
            value={payload.email}
            placeholder={"Email"}
            onChange={handleChange}
            type="email"
            name={"email"}
          />

          <Input
            value={payload.password}
            placeholder={"Password"}
            name={"password"}
            type="password"
            onChange={handleChange}
          />

          <Input
            value={payload.password_confirmation}
            placeholder={"Confirm Password"}
            name={"password_confirmation"}
            type="password"
            onChange={handleChange}
          />

          <div className="flex flex-row justify-between">
            <Button
              onClick={() => {
                return navigate("/login", { replace: true });
              }}
              title={"Back"}
            />
            <Button title={isLoading ? "Proses" : "Register"} />
          </div>
        </form>
      </div>
    </div>
  );
};

export default Register;
