import React, { useState } from "react";
import RefTutorial from "./pages/useRef";
import Reducer from "./pages/useReducer";
import UseLayoutEffect from "./pages/useLayoutEffect";
import Parent from "./pages/parent";
import usePage from "./hook/hookUsePage";

function App() {
  const {page, setPage, pageSize, setPageSize} = usePage();
  const {alquran, setAlquran} = useList()
  const handlePageSize = (e) => {
    setPageSize(e.target.value);
  };
  const handlePage = (e) => {
    setPage(e.target.value);
  };
  return (
    <React.Fragment>
      {/* <RefTutorial /> */}
      {/* <UseLayoutEffect /> */}
      {/* <Parent /> */}
      {/* <Reducer></Reducer> */}
      <div className="p-10 space-y-5 font-mono">
        <h1 className="font-bold">Belajar Custom Hook</h1>
        <div className="flex space-x-10">
          <section>
            <p>page size: {pageSize}</p>
            <select name="" id="" onChange={handlePageSize} value={pageSize}>
              <option value={5}>5</option>
              <option value={10}>10</option>
              <option value={25}>25</option>
              <option value={50}>50</option>
            </select>
          </section>
          <section>
            <p>page: {page}</p>
            <div className="space-x-5 underline">
              <button onClick={handlePage} value={1}>
                1
              </button>
              <button onClick={handlePage} value={2}>
                2
              </button>
              <button onClick={handlePage} value={3}>
                3
              </button>
              <button onClick={handlePage} value={4}>
                4
              </button>
              <button onClick={handlePage} value={5}>
                5
              </button>
            </div>
          </section>
        </div>
      </div>
    </React.Fragment>
  );
}

export default App;
