import React from "react";
import Skeleton from "react-loading-skeleton";
import { deleteArticle, getArticle } from "../../API/article";
import { useNavigate } from "react-router-dom";
import Button from "../../komponen/button";
import { useDispatch, useSelector } from "react-redux";
import { increment, decrement } from "../redux/action/countAction";

export default function Article() {
  const navigate = useNavigate();
  const [listArticle, setListArticle] = React.useState([]);
  const [isFetchArticle, setIsFetchArticle] = React.useState(false);
  const [page, setPage] = React.useState(100);
  const store = useSelector((state) => state);
  const count = useSelector((state) => state.count);
  const nama = useSelector((state) => state.authProcess);
  const dispatch = useDispatch();

  const getListArticleHandle = async () => {
    try {
      setIsFetchArticle(true);
      const response = await getArticle();
      console.log(response.data);
      setListArticle(response.data.data.data);
    } catch (err) {
      console.log(err);
    } finally {
      setIsFetchArticle(false);
    }
  };
  console.log(listArticle);

  React.useEffect(() => {
    getListArticleHandle();
  }, []);
  return (
    <div className="p-3">

        {" "}
        <p className="mr-2">name: {nama.name}</p>
        <p>email: {nama.email}</p>

      <div className="grid grid-cols-7 gap-5 ">
        <Button
          title={"Tambah Artikel"}
          onClick={() => {
            return navigate("/article/createArticle", { replace: true });
          }}
        />
          <div className="flex flex-col">
          status: {count.status}
          value: {count.value}
        </div>

        <Button
          onClick={() => {
            dispatch(decrement());
          }}
          title={"Previous"}
        />

        <Button
          onClick={() => {
            dispatch(increment());
          }}
          title={"Next"}
        />

        <Button
          title={"green"}
          color="green"
          onClick={() => {
            dispatch({ type: "change", color: "#32a862" });
          }}
        />

        <Button
          title={"yellow"}
          color="yellow"
          onClick={() => {
            dispatch({ type: "change", color: "#e9f542" });
          }}
        />
        <Button
          title={"kembali"}
          color="#"
          onClick={() => {
            dispatch({ type: "return" });
          }}
        />
      </div>
      <table className="table-auto w-full mt-2 ">
        <thead>
          <tr className="text-left border">
            <th>No</th>
            <th>Judul</th>
            <th>Thumbnail</th>
            <th>Article</th>
            <th>Penulis</th>
            <th>id</th>
            <th>Dibuat</th>
            <th>Diupdate</th>
            <th>Aksi</th>
          </tr>
        </thead>
        <tbody>
          {isFetchArticle ? (
            <tr>
              <td colSpan={9}>
                <Skeleton
                  baseColor="white"
                  highlightColor="grey  "
                  height={80}
                  count={20}
                />
              </td>
            </tr>
          ) : 
            <div></div>
            })
          )}
        </tbody>
      </table>
    </div>
  );
}
